import {useState} from 'react';
import {useRouter} from 'next/router';
import Jumbotron from '../components/Jumbotron';
import Navbar from '../components/Navbar';
import About from '../components/About';
import Skills from '../components/Skills';
import Recent from '../components/Recent';
import { route } from 'next/dist/next-server/server/router';


export default function Index() {
  const router = useRouter();
  const [state, setstate] = useState({path : ``})
  if(router.pathname === '/') setstate({path : '/'})
  else setstate({path : router.pathname})
  return (
    <React.Fragment>
      <Jumbotron/>
      <Navbar path={state}/>
      <About />
      <Skills/>
      {/* <Recent /> */}
      <style jsx global>{`
        @import url('https://fonts.googleapis.com/css2?family=Alice&family=Lato:wght@300&family=Open+Sans:wght@300&display=swap');
        * {
          font-family: 'Open Sans', sans-serif,'Alice', serif,'Lato', sans-serif;
        }
        body {
          font-size: 15px;
        }
        em {
          font-weight: bold;
        }
        .line {
          width: 2rem;
          border: 5px solid #A80606;
          border-radius: 100px;
          display: inline;
        }
        .about h2 {
          margin: 0;
        }
      `}</style>
    </React.Fragment>
  )
}
