import portofolio from '../static/images/Porto';

export default function Recent(){
    let data = portofolio.portofolio;
    return (
        <div className="recent">
          <h2>RECENT WORKS</h2>
          <section>
            {data.map((item,index) => (
            <figure key={index}>
                <img src={item.gambar} alt={item.gambar}/>
            </figure>
            ))}
        </section>
      <style jsx>{`
      .recent {
          padding: 0 4rem;

      }
        section {
            display: grid;
            grid-template-columns: repeat(3,1fr);
            grid-gap: 2rem;
            margin-top: 20px;
        }
        figure {
            margin: 0;
        }
        figure img {
            width: 22rem;
            height: 13rem;
        }
        h2{
            text-align: center;
            font-family: 'Alice',serif;
        }
      `}</style>
      </div>
    )
}
