import footerImg from '../static/images/FOOTER.png';
import twImg from '../static/images/TWIITER.png'
import fbImg from '../static/images/FACEBOOK.png'
import igImg from '../static/images/INSTAGRAM.png'
import gitImg from '../static/images/GITHUB.png'

const footer = () => {
    return (
        <footer>
            <main>
                <div className="social">
                    <h2>Social Media</h2>
                    <div>
                        <img src={twImg} alt={twImg} />
                        <img src={fbImg} alt={fbImg} />
                        <img src={igImg} alt={igImg} />
                        <img src={gitImg} alt={gitImg} />
                    </div>
                    <p>&copy; Bayu Dirgantara 2020</p>
                </div>

                <iframe title="address" src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d127669.0495089475!2d101.37113474552513!3d0.513962478911432!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31d5ab80690ee7b1%3A0x94dde92c3823dbe4!2sPekanbaru%2C%20Kota%20Pekanbaru%2C%20Riau!5e0!3m2!1sid!2sid!4v1583780728791!5m2!1sid!2sid"></iframe> 
            </main>
            {/* <div>
                <p>&copy; Bayu Dirgantara 2020.</p>
            </div> */}
        <style jsx>{`
            footer {
                margin-top: 2rem;
                background-color: #eee;
                border-top: 1px solid #000;
                border-top-right-radius: 10px;
                border-top-left-radius: 10px;
            }
            main {
                height: 20rem;
                display: flex;
            }
            main .social {
                display: flex;
                text-align: center;
                width: 30%;
                color: #000000;
                font-family: 'Alice',serif;
            }
            .social div img {
                background-color: #eeeeee;
                width: 40px;
                height: 40px;
            }
            main .social p {
                align-self: flex-end;
            }
            main > iframe {
                border-radius: 10px;
                width:1500px;
                height: auto;
                frameborder:0; 
                border:0; 
                allowfullscreen:"";
            }
        `}</style>
        </footer>
    )
}

export default footer;
