import Jumbotron from '../components/Jumbotron';
import Navbar from '../components/Navbar';
import About from '../components/About';
import Tools from '../components/Tools';
import portofolio from '../static/images/Porto';


export default function Index() {
    console.log(portofolio.portofolio);
    let data = portofolio.portofolio;
  return (
    <div>
      <Jumbotron/>
      <Navbar/>
      <About />
      <Tools/>
      <div className="recent">
          <h2>RECENT WORKS</h2>
          <section>
      {data.map((item,index) => (
          <figure key={index}>
          <img src={item.gambar} alt={item.gambar}/>
            <figcaption><h5>{item.caption}</h5></figcaption>
            </figure>
      ))}
      </section>
      </div>
      <style jsx>{`
      .recent {
          padding: 0 60px;

      }
        section {
            display: grid;
            grid-template-columns: repeat(3,1fr);
            grid-gap: 30px;
        }
        figure {
            margin: 0;
        }
        h2{
            text-align: center;
        }
      `}</style>
    </div>
  )
}
