import portofolio from '../static/images/Porto';

export default function Recent(){
    let data = portofolio.portofolio;
    return (
        <div className="recent">
          <h2>RECENT WORKS</h2>
          <section>
            {data.map((item,index) => (
            <figure key={index}>
                <img src={item.gambar} alt={item.gambar}/>
            </figure>
            ))}
        </section>
      <style jsx>{`
      .recent {
          padding: 0 60px;

      }
        section {
            display: grid;
            grid-template-columns: repeat(3,1fr);
            grid-gap: 30px;
            margin-top: 20px;
        }
        figure {
            margin: 0;
            width: 340px;
            height: 135px;
        }
        h2{
            text-align: center;
        }
      `}</style>
      </div>
    )
}
