import img from '../static/images/ABOUT.png';

const about = () => (
  <div className="about">
      <h2>ABOUT</h2>
      <div className="container">
          <span><p>sdawdsd</p></span>
          <img src={img} alt={img} />
      </div>
      <style jsx>{`
        .about {
            text-align: center;
        }
        h2 {
            font-family: 'Alice',serif;
        }
        .container {
            display: grid;
            grid-template-columns: repeat(2,30%);
            justify-content: center;
            grid-column-gap: 40px;
        }
        span {
            text-align: right;
            align-self: center;
        }
      `}</style>
  </div>
)

export default about;
