import htmlimg from '../static/images/HTML.png';
import cssimg from '../static/images/CSS.png';
import jsimg from '../static/images/JAVASCRIPT.png';
import nodeimg from '../static/images/NODE.png';
import reactimg from '../static/images/REACT.png';
import bootimg from '../static/images/BOOTSTRAP.png'

export default function Skills(){
    return (
        <div className="skills">
          <h2>SKILLS</h2>
          <div className="container">
            <figure>
                <img src={htmlimg} alt={htmlimg}/>
                <h2>HTML 5</h2>
            </figure>
            <figure>
                <img src={cssimg} alt={cssimg}/>
                <h2>CSS 3</h2>
            </figure>
            <figure>
                <img src={jsimg} alt={jsimg}/>
                <h2>JAVASCRIPT</h2>
            </figure>
            <figure>
                <img src={bootimg} alt={jsimg}/>
                <h2>BOOTSTRAP</h2>
            </figure>
            <figure>
                <img src={reactimg} alt={reactimg}/>
                <h2>REACT</h2>
            </figure>
            <figure>
                <img src={nodeimg} alt={nodeimg}/>
                <h2>NODEJS</h2>
            </figure>
          </div>
          <style jsx>{`
            .skills {
                margin-top: 2rem auto 0 auto;
            }
            .skills > h2 {
                text-align: center;
                margin: 0;
                font-family: 'Alice',serif;
            }
            .container {
                margin-top: 1.5rem;
                display: grid;
                grid-template-columns: repeat(3,auto);
                justify-content: center;
                grid-column-gap: 13px;
                grid-row-gap: 1.5rem;
            }
            figure {
                margin: 0;
                display: flex;
                border: 1px solid #918080;
                width: 15rem;
                border-radius: 1rem;
                box-shadow: 0px 5px 10px -1px #ccc;
            }
            figure :hover {
                background-color: #eeeeee;
                cursor: pointer;
            }
            figure img {
                width: 40px;
                height: 40px;
                align-self: center;
                padding: 0 15px  0 15px;
            }
            figure > h2 {
                font-size: 1.2rem;
                font-family: 'Alice',serif;
                font-weight: 200;
            }
          `}</style>
      </div>
    )
}
