import img from '../static/images/ABOUT.png';
import imgBtn from '../static/images/paper-plane.svg';

const about = () => (
  <div className="about">
      <h2>ABOUT</h2>
      <div className="container">
          <span>
              <p>Hi, saya Bayu Dirgantara, Frontend Developer - Pekanbaru</p>
              <p>Saya sangat tertarik dengan web developer dan yang terkait dengan “pemograman”</p>
              <p>Saya belajar pemograman sebagai Frontend Developer secara “otodidak”</p>
              <p>Saya siap implementasi project with “wordenful people”</p>
              <button><img src={imgBtn} alt={imgBtn}/><p>Contact me</p></button>
              </span>
          <img src={img} alt={img} />
      </div>
      <style jsx>{`
        .about {
            text-align: center;
        }
        h2 {
            font-family: 'Alice',serif;
        }
        .container {
            display: grid;
            grid-template-columns: repeat(2,40%);
            justify-content: center;
            grid-column-gap: 50px;
            padding-left: 150px;
        }
        span {
            text-align: right;
            align-self: center;
        }
        button img {
            width: 24px;
            height: 24px;
        }
        button {
            width: 140px;
            display: flex;
            align-items: center;
            padding: 2px 15px;
            border-radius: 20px;
        }
      `}</style>
  </div>
)

export default about;
