import portofolio from '../static/images/Porto';
import { useRef, useEffect } from 'react';

export default function Recent(){
    let data = portofolio.portofolio;
    useEffect(()=>{
        handleImg;
    })
    const button = useRef(null)

    const handleImg = (e)=> {
        console.log(e.currentTarget)
        // e.target.style.opacity = '20%';
        // console.log(button.current.hidden)
        button.current.classList.add('toogle')
    }
const handleLeave = (e) => {
    console.log(e)
}

    return (
        <div className="recent">
          <h2>RECENT WORKS</h2>
          <section>
            {data.map((item,index) => (
            <figure key={index}>
                <img className="thumb" src={item.gambar} alt={item.gambar} onMouseEnter={handleImg}/>
                <button ref={button}>See Detail</button>
            </figure>
            ))}
        </section>
      <style jsx>{`
      .recent {
          padding: 0 4rem;
          margin-top: 3.5rem

      }
        section {
            margin-top: 3rem;
            display: grid;
            grid-template-columns: repeat(3,1fr);
            grid-gap: 2rem;
        }
        figure {
            margin: 0 auto;
        }
        figure img {
            width: 22rem;
            height: 13rem;
            border-radius: 10px;
        }
        figure button {
            display: none;
            z-index: 2;
            position: relative;
            bottom: 30%
        }
        .toogle {
            display: block;
        }
        h2{
            text-align: center;
            font-family: 'Alice',serif;
            margin: 0;
        }
      `}</style>
      </div>
    )
}
