import { useState, useRef, useEffect } from 'react';

const gallery = (props) => {
    const [isStatus,setIsStatus] = useState(false);
    const [opacity,setOpacity] = useState(null);
    const img = useRef('');
    const button = useRef('');
    // console.log(props)
    function updateUI(e){
        e.preventDefault();
        // console.log(e)
        if(e.type === 'mouseenter'){
            setIsStatus(true)
        }else if(e.type === 'mouseleave'){
            setIsStatus(false)
        }
    }


    useEffect(()=>{
        function getStyle(){
            if(isStatus === false){
                // console.log(img.current)
                img.current.style.opacity = '100%';
                button.current.style.display = 'block';
            }else {
                button.current.style.display = 'none';
                img.current.style.opacity = '20%';
            }
        }
        getStyle();
    })

    console.log(button.current)

    return (
        <figure>
            <img ref={img} src={props.data.gambar}alt={props.data.gambar} onMouseEnter={updateUI} onMouseLeave={updateUI}/>  
            <div key={props.data.gambar}><button ref={button}>See Detail</button></div>
        <style jsx>{`
            figure {
                margin: 0;
                height: auto;
            }
            figure img {
                width: 22rem;
                height: 13rem;
                border-radius: 10px;
                display: block;
            }
            figure div {
                display: block;
                position: absolute;
                bottom: -1000px;
            }
        `}</style>
        </figure>
    )
}

export default gallery;
