import { useState, useRef, useEffect } from 'react';

const gallery = (props) => {
    const [isStatus,setIsStatus] = useState(false);
    const [opacity,setOpacity] = useState(null);
    const img = useRef('');
    const button = useRef('');
    // console.log(props)
    function updateUI(e){
        e.preventDefault();
        // console.log(e)
        if(e.type === 'mouseenter'){
            setIsStatus(true)
        }else if(e.type === 'mouseleave'){
            setIsStatus(false)
        }
    }


    // useEffect(()=>{
