    //             button.current.style.display = 'none';
    //             img.current.style.opacity = '20%';
    //         }
    //     }
    //     getStyle();
    // })

    return (
        <figure>
            <img ref={img} src={props.data.gambar}alt={props.data.gambar} onMouseEnter={updateUI} onMouseLeave={updateUI}/>  
            <div className="title"><button>See Detail</button></div>
        <style jsx>{`
            figure {
                margin: 0;
                height: auto;
                width: auto;
                box-sizing: border-box;
                float: left;
            }
            figure img {
                width: 22rem;
                height: 13rem;
                border-radius: 10px;
                display: block;
            }
            figure .title {
                position: absolute;
                top: 10px;
            }
        `}</style>
        </figure>
    )
}

export default gallery;
