import { useState } from 'react';

const gallery = (props) => {
    const [isStatus,setIsStatus] = useState(false);
    console.log(props)
    function updateUI(e){
        e.preventDefault();
        console.log(e)
        if(e.type === 'mouseenter'){
            setIsStatus(true)
        }else if(e.type === 'mouseleave'){
            setIsStatus(false)
        }
    }
    return (
        <figure>
            <img  src={props.data.gambar}alt={props.data.gambar} onMouseEnter={updateUI} onMouseLeave={updateUI}/> 
            <button >See Detail</button>
        </figure>
    )
}

export default gallery;
