import jumbotron from '../static/images/JUMBOTRON.png'
import myimage from '../static/images/SAYA.jpg'

export default function Jumbotron(){
    return (
            <main>
                <div className="profile">
                    <img src={myimage} alt={myimage}/>
                    <h1>Bayu Dirgantara</h1>
                    <p>Frontend Developer</p>
                    <p>bayuusetiawan86@gmail.com</p>
                </div>
            <style jsx global>{`
                main {
                    background: url(${jumbotron});
                    min-height: 455px;
                    display: flex;
                    justify-content: center;
                }
                .profile {
                    text-align: center;
                    align-self: center;
                    align-content: center;
                    color: #EEEEEE;
                }
                h1 {
                    color: #FFFFFF;
                    margin: 0;
                    margin-bottom: 14px;
                }
                p {
                    margin: 0
                }
                img {
                    width: 140px;
                    height: 140px;
                    border-radius: 100%;
                }
            `}</style>
            </main>
    )
}
