import img from '../static/images/ABOUT.png';

const about = () => (
  <div className="about">
      <h2>ABOUT</h2>
      <div className="container">
          <span><p>Hi, saya Bayu Dirgantara, Frontend Developer -Pekanbaru

Saya sangat tertarik dengan web developer dan yang terkait dengan
 “pemograman”

Saya belajar pemograman sebagai Frontend Developer secara “otodidak”

Saya siap implementasi project with “wordenful people”</p></span>
          <img src={img} alt={img} />
      </div>
      <style jsx>{`
        .about {
            text-align: center;
        }
        h2 {
            font-family: 'Alice',serif;
        }
        .container {
            display: grid;
            grid-template-columns: repeat(2,30%);
            justify-content: center;
            grid-column-gap: 50px;
            padding-left: 150px;
        }
        span {
            text-align: right;
            align-self: center;
        }
      `}</style>
  </div>
)

export default about;
