import portofolio from '../static/images/Porto';
import { useRef, useEffect } from 'react';

export default function Recent(){
    let data = portofolio.portofolio;

    const handleImg = (e)=> {
        console.log(e.target)
        e.target.hover.opacity = '20%';
    }
    return (
        <div className="recent">
          <h2>RECENT WORKS</h2>
          <section>
            {data.map((item,index) => (
            <figure key={index}>
                <img src={item.gambar} alt={item.gambar} onMouseEnter={handleImg}/>
                <button>See Detail</button>
            </figure>
            ))}
        </section>
      <style jsx>{`
      .recent {
          padding: 0 4rem;
          margin-top: 3.5rem

      }
        section {
            margin-top: 3rem;
            display: grid;
            grid-template-columns: repeat(3,1fr);
            grid-gap: 2rem;
        }
        figure {
            margin: 0 auto;
        }
        figure img {
            width: 22rem;
            height: 13rem;
            border-radius: 10px;
        }
        figure button {
            z-index: 2;
            position: relative;
            bottom: 30%
        }
        h2{
            text-align: center;
            font-family: 'Alice',serif;
            margin: 0;
        }
      `}</style>
      </div>
    )
}
