import img from '../static/images/ABOUT.png';
import imgBtn from '../static/images/paper-plane.svg';

const about = () => (
  <div className="about">
      <h2>ABOUT</h2>
      <div className="container">
          <span>
              <p>Hi, saya <em>Bayu Dirgantara</em>, Frontend Developer - Pekanbaru</p>
              <p>Saya sangat tertarik dengan web developer dan yang terkait <br/>dengan <em>“pemograman”</em></p>
              <p>Saya belajar pemograman sebagai Frontend Developer <br/>secara <em>“otodidak”</em></p>
              <p>Saya siap implementasi project with <em>“wonderful people”</em></p>
              <button><img src={imgBtn} alt={imgBtn}/><font>Contact me</font></button>
              </span>
          <img src={img} alt={img} />
      </div>
      <style jsx>{`
        .about {
            margin-top: 2rem;
            text-align: center;
        }
        h2 {
            font-family: 'Alice',serif;
        }
        .container {
            margin-top: 1.5rem;
            display: grid;
            grid-template-columns: repeat(2,45%);
            justify-content: center;
            grid-column-gap: 50px;
            padding-left: 7.5rem;
        }
        .container > img {
            box-shadow: 0 8px 6px -10px black;
        }
        span {
            text-align: right;
            align-self: center;
            font-size: 12px !important;
        }
        button img {
            width: 20px;
            height: 20px;
            padding-right: 6px;
        }
        button {
            width: 140px;
            height: 40px;
            display: flex;
            border: 1px solid #000000;
            padding: 4px 1rem;
            border-radius: 10px;
            margin-left: auto;
            background-color: #ffffff;
            margin-top: 15px;
        }
        button:hover {
            background-color: #eeeeee;
        }
        button p {
            margin: 0;
        }
      `}</style>
  </div>
)

export default about;
