import portofolio from '../static/images/Porto';
import { useRef } from 'react';

export default function Recent(){
    let data = portofolio.portofolio;
    const img = useRef(null)
    const handleImg = ()=> {
        console.log('oke')
        console.log(img);
    }
    return (
        <div className="recent">
          <h2>RECENT WORKS</h2>
          <section>
            {data.map((item,index) => (
            <figure key={index}>
                <img src={item.gambar} alt={item.gambar} ref={img} onMouseEnter={handleImg}/>
            </figure>
            ))}
        </section>
      <style jsx>{`
      .recent {
          padding: 0 4rem;
          margin-top: 3.5rem

      }
        section {
            margin-top: 3rem;
            display: grid;
            grid-template-columns: repeat(3,1fr);
            grid-gap: 2rem;
        }
        figure {
            margin: 0 auto;
        }
        figure img {
            width: 22rem;
            height: 13rem;
            border-radius: 10px;
        }
        figure img:hover {
            opacity: 20%;
        }
        h2{
            text-align: center;
            font-family: 'Alice',serif;
            margin: 0;
        }
      `}</style>
      </div>
    )
}
