import { useState, useRef, useEffect } from 'react';

const gallery = (props) => {
    const [isStatus,setIsStatus] = useState(false);
    const [opacity,setOpacity] = useState(null);
    const img = useRef(this);
    // console.log(props)
    function updateUI(e){
        e.preventDefault();
        // console.log(e)
        if(e.type === 'mouseenter'){
            setIsStatus(true)
        }else if(e.type === 'mouseleave'){
            setIsStatus(false)
        }
    }

    useEffect(()=>{
        if(isStatus === false){
            console.log(img)
        }else {
            return {opacity: `20%`}
        }
    })

    return (
        <figure>
            <img ref={img} src={props.data.gambar}alt={props.data.gambar} onMouseEnter={updateUI} onMouseLeave={updateUI}/>  
            <button >See Detail</button>
        <style jsx>{`
            figure {
                margin: 0 auto;
            }
            figure img {
                width: 22rem;
                height: 13rem;
                border-radius: 10px;
            }
            figure button {
                z-index: 2;
                position: relative;
                bottom: 30%
            }
        `}</style>
        </figure>
    )
}

export default gallery;
