import img from '../public/static/ABOUT.png';
import imgBtn from '../public/static/paper-plane.svg';
import myimage from '../public/static/SAYA.jpg'
import { useRef, useEffect, useState } from 'react';

const about = () => {
    const refimg = useRef('');
    useEffect(()=>{
        let media = window.matchMedia(`(max-width: 736px)`).matches;
        // if(media == true){
        //     refimg.current.setAttribute('src',myimage);
        // }
    })
    return (
        <div className="container about mt-5">
            <h3 className="text-center">About Me</h3>
            <div className="row mt-3">
                <div className="col d-flex align-items-center justify-content-end">
                <section className="text-right">
                   <p>Hi, saya <em>Bayu Dirgantara</em>, FullStack Web Developer - Pekanbaru</p>
                   <p>Saya sangat tertarik dengan web developer dan yang terkait dengan <em>“pemograman”</em></p>
                   <p>Saya belajar pemograman sebagai FullStack Web Developer secara <em>“otodidak”</em></p>
                   <p>Saya siap implementasi project with <em>“wonderful people”</em></p>
                 <button className="btn btn-outline-secondary mt-3" type="button"><img src={imgBtn} alt={imgBtn}/><font>Contact me</font></button>
               </section>
                </div>
                <div className="col">
                <img ref={refimg} src={img} alt={img}/>
                </div>
            </div>
            <style jsx>{`
                button {
                    box-shadow: 0px 5px 10px -1px #ccc;
                    border-radius: 10px;
                }
                button img {
                    width: 25px;
                    padding-right: 6px;
                }
            `}</style>
        </div>
    )
}

export default about;


